mapper.select:
select rep.reportId,rep.reportName,rep.carid,rep.price,rep.sex,rep.age,rep.phone,left(rep.carid,6) as carido,right(rep.carid,3) as caridt,reg.type,DATE_FORMAT(rep.time,'%Y-%m-%d') as datime,DATE_FORMAT(rep.time,'%Y-%m-%d') as time,dep.department,doc.doctorName,rep.price,reg.type,rep.users,rep.zhuan,rep.sex,doc.departmentId as ddepartmentid,rep.state,doc.doctorId from report as rep left join registeredtype reg on(rep.reportType=reg.registeredId) left join departments dep on(dep.departmentId=rep.department) left join doctor doc on(rep.doctor=doc.doctorId) where 1=1 and rep.state=#{states}
and to_days(now())-to_days(rep.time)<=0
and to_days(rep.time)=TO_DAYS(now())
and to_days(rep.time)-to_days(now())>=1
and rep.reportName like '%${cc}%'
limit ${(page-1)*limit},${limit}

select rep.reportId,rep.reportName,rep.sex,rep.age,rep.price,rep.state,DATE_FORMAT(rep.time,'%Y-%m-%d') as time,rep.phone,rep.carid,rep.zhuan,rep.users ,dep.department,doc.doctorName,regtype.type from report as rep left join departments as dep on(dep.departmentId=rep.department) left join doctor as doc on(doc.doctorId=rep.doctor) left join registeredtype as regtype on(regtype.registeredId=rep.reportType)
and rep.reportName like '%${name}%'
limit ${(page-1)*limit},${limit}
